def is_jokey(user_text: str, bot_text: str) -> bool:
    ut = (user_text or "").strip().lower()
    bt = (bot_text or "").strip().lower()
    if ut.startswith(("/meme", "/caption")):
        return True
    t = f"{ut} {bt}"
    return any(h in t for h in JOKE_HINTS)

